    function cancelOrder() {
        const idToCancel = cancelOrderInput.value;
        
        if (!idToCancel) {
            alert('Bitte geben Sie eine ID zum Stornieren ein.');
            return;
        }
        
        // Load data from localStorage
        loadDataFromStorage();
        
        // Track if we found and removed the item
        let found = false;
        
        // Try to find and remove from Tour 1 data
        const initialLengthTour1 = warenausgangData_tour1.length;
        warenausgangData_tour1 = warenausgangData_tour1.filter(item => item.id !== idToCancel);
        
        if (warenausgangData_tour1.length < initialLengthTour1) {
            // Save updated Tour 1 data
            localStorage.setItem('warenausgangData_tour1', JSON.stringify(warenausgangData_tour1));
            found = true;
            alert(`Auftrag mit ID ${idToCancel} wurde aus Tour 1 storniert.`);
        }
        
        // If not found in Tour 1, try Tour 2 data
        if (!found) {
            const initialLengthTour2 = warenausgangData.length;
            warenausgangData = warenausgangData.filter(item => item.id !== idToCancel);
            
            if (warenausgangData.length < initialLengthTour2) {
                // Save updated Tour 2 data
                localStorage.setItem('warenausgangData', JSON.stringify(warenausgangData));
                found = true;
                alert(`Auftrag mit ID ${idToCancel} wurde aus Tour 2 storniert.`);
            }
        }
        
        // If found in either tour, clear input and focus
        if (found) {
            cancelOrderInput.value = '';
            tourInput.focus();
        } else {
            alert(`Keine Auftrag mit ID ${idToCancel} gefunden.`);
        }
    }
    
    
