    function parseWeightData(data) {
        // Add data to buffer
        dataBuffer += data;
        
        // Look for complete weight readings in the buffer
        // Based on your output: "8.0 kg 48.0 kg G"
        // It seems your scale sends weight readings that end with unit + possible status
        
        //console.log(`Buffer content: "${dataBuffer}"`);
        
        // Look for patterns that indicate complete weight readings
        const patterns = [
            /(\d+\.?\d*)\s*kg/gi,  // Weight followed by kg
            /(\d+\.?\d*)\s*g(?!\s*k)/gi,   // Weight followed by g (but not kg)
            /(\d+\.?\d*)\s*lb/gi,  // Weight followed by lb
        ];
        
        let foundWeight = null;
        let foundUnit = 'kg';
        
        for (let pattern of patterns) {
            pattern.lastIndex = 0; // Reset regex
            const matches = [...dataBuffer.matchAll(pattern)];
            
            if (matches.length > 0) {
                // Take the last (most recent) weight reading
                const lastMatch = matches[matches.length - 1];
                foundWeight = parseFloat(lastMatch[1]);
                foundUnit = lastMatch[0].includes('kg') ? 'kg' : 
                          lastMatch[0].includes('lb') ? 'lb' : 'g';
                //foundUnit = lastMatch[0].includes('kg') ? 'kg' :'g';

                
                //console.log(`Parsed weight: ${foundWeight} ${foundUnit}`);
                //log(`total matches found: ${matches.length}`);
                break;
            }
        }
        
        // Clear buffer if it gets too long or contains complete data
        if (dataBuffer.length > 100 || foundWeight !== null) {
            dataBuffer = '';
        }
        
        return { 
            weight: foundWeight, 
            unit: foundUnit, 
            raw: data 
        };
    }

    async function connectToScale() {
        try {
            //hideError();
            console.log('Requesting serial port access...');
            
            // Request access to serial port
            port = await navigator.serial.requestPort();
            
            console.log('Opening connection to scale...');
            
            // Open the serial port with common Ohaus settings
            await port.open({
                baudRate: 9600,  // Most common for Ohaus scales
                dataBits: 8,
                stopBits: 1,
                parity: 'none',
                bufferSize: 1024
            });
            
            isConnected = true;
            updateUI(true);
            console.log('Successfully connected to scale!');
            
            // Start reading data
            await readFromScale();
            
        } catch (error) {
            //console.log(`Failed to connect: ${error.message}`);

            isConnected = false;
            updateUI(false);
        }
    }

    async function readFromScale() {
        if (!port || !port.readable) return;
        
        try {
            reader = port.readable.getReader();
            
            // Start requesting weight data periodically for real-time updates
            //weightRequestInterval = setInterval(async () => {
            //    if (isConnected) {
            //       await requestWeight();
            //    }
            //}, 1000); // Request weight every 1 second
            
            while (isConnected) {
                const { value, done } = await reader.read();
                if (done) break;
                
                const text = new TextDecoder().decode(value);
                //console.log(`Raw data received: "${text}"`);
                
                const parsed = parseWeightData(text);
                
                if (parsed.weight !== null) {
                    // Update the weight display div
                    //weightDisplayEl.textContent = `${parsed.weight} ${parsed.unit}`;
                    //document.getElementById('wagen_gewicht_nummer').value = parsed.weight;
                    gewichtInput.value = parsed.weight;
                    //console.log(`â Weight updated: ${parsed.weight} ${parsed.unit}`);
                }
            }
            
        } catch (error) {
            console.log(`Reading error: ${error.message}`);
            await disconnectFromScale();
        } finally {
            if (reader) {
                reader.releaseLock();
            }
        }
    }
    
    async function requestWeight() {
        if (!port || !port.writable || !isConnected) {
            console.log('â ï¸ Cannot request weight - port not available');
            return;
        }
        
        try {
            const writer = port.writable.getWriter();
            
            // Try different commands that might work with your scale
            // Based on Ohaus documentation, try these commands:
            const commands = ['P\r\n', 'IP\r\n', '\r\n'];  // P=Print, IP=Immediate Print, or just Enter
            
            // Try the first command (P for Print)
            const command = commands[1];
            
            await writer.write(new TextEncoder().encode(command));
            await writer.releaseLock();
            
            //console.log(`ð¤ Weight request sent: "${command.replace(/\r\n/g, '\\r\\n')}"`);
            
        } catch (error) {
            console.log(`â Weight request failed: ${error.message}`);
        }
    }
    
    async function disconnectFromScale() {
        try {
            isConnected = false;
            
            // Clear the interval
            if (weightRequestInterval) {
                clearInterval(weightRequestInterval);
                weightRequestInterval = null;
            }
            
            if (reader) {
                await reader.cancel();
                reader.releaseLock();
                reader = null;
            }
            
            if (port) {
                await port.close();
                port = null;
            }
            
            // Clear data buffer
            dataBuffer = '';
            
            updateUI(false);
            console.log('Disconnected from scale');
            hideError();
            
        } catch (error) {
            console.log(`Disconnect error: ${error.message}`);
        }
    }

